import React, { useState } from "react";
import StudentForm from "./StudentForm";

type Student = {
    name: string;
    className: string;
    age: string;
    vaccinationStatus: string;
};

const StudentList: React.FC = () => {
    const [students, setStudents] = useState<Student[]>(initialStudents);

    // For add/edit drawer
    const [drawerOpen, setDrawerOpen] = useState(false);
    const [editingStudent, setEditingStudent] = useState<Student | null>(null);

    // Handle opening the drawer for adding a student
    const handleAddStudent = () => {
        setEditingStudent(null);    // No student selected -> add new
        setDrawerOpen(true);
    };

    // Handle opening the drawer for editing a student
    const handleEditStudent = (student: Student) => {
        setEditingStudent(student); // Set the student to edit
        setDrawerOpen(true);
    };

    // Handle form submit for add/edit
    const handleFormSubmit = (form: Student) => {
        if (editingStudent) {
            // Edit mode: update student in list
            setStudents(students.map(s =>
                s.name === editingStudent.name && s.className === editingStudent.className // or use a real unique id
                    ? form
                    : s
            ));
        } else {
            // Add mode: append new student
            setStudents([...students, form]);
        }
    };

    return (
        <div>
            <h2>Students</h2>
            <button onClick={handleAddStudent}>Add Student</button>
            <ul>
                {students.map((s, idx) => (
                    <li key={idx}>
                        {s.name} - {s.className} - {s.age} - {s.vaccinationStatus}
                        <button onClick={() => handleEditStudent(s)} style={{marginLeft: 8}}>Edit</button>
                    </li>
                ))}
            </ul>
            <StudentForm
                open={drawerOpen}
                onClose={() => setDrawerOpen(false)}
                onSubmit={handleFormSubmit}
                student={editingStudent}
            />
        </div>
    );
};

export default StudentList;
