import React, { useState } from "react";
import StudentForm from "./StudentForm"; // Adjust import as needed

// Sample Student type & list:
type Student = {
    name: string;
    className: string;
    age: string;
    vaccinationStatus: string;
};

const initialStudents: Student[] = [
    {
        name: "Alice",
        className: "10A",
        age: "15",
        vaccinationStatus: "Vaccinated"
    },
    // ... more students
];

const StudentList: React.FC = () => {
    const [students, setStudents] = useState<Student[]>(initialStudents);
    const [drawerOpen, setDrawerOpen] = useState(false);
    const [editingStudent, setEditingStudent] = useState<Student | null>(null);

    // When add button is clicked
    const handleAddClick = () => {
        setEditingStudent(null);        // make sure it's null
        setDrawerOpen(true);
    }

    // When edit button is clicked
    const handleEditClick = (student: Student) => {
        setEditingStudent(student);     // set student before opening
        setDrawerOpen(true);
    };

    const handleFormSubmit = (data: Student) => {
        if (editingStudent) {
            // Update student in list (in real app: use id for matching)
            setStudents(students.map(s =>
                s.name === editingStudent.name ? data : s
            ));
        } else {
            setStudents([...students, data]);
        }
    };

    return (
        <div>
            <button onClick={handleAddClick}>Add Student</button>
            <ul>
                {students.map((s, idx) => (
                    <li key={idx}>
                        {s.name} ({s.className})
                        <button onClick={() => handleEditClick(s)}>Edit</button>
                    </li>
                ))}
            </ul>
            <StudentForm
                open={drawerOpen}
                onClose={() => setDrawerOpen(false)}
                onSubmit={handleFormSubmit}
                student={editingStudent}
            />
        </div>
    );
};

export default StudentList;
