import React, { useState } from 'react';
import axios from 'axios';

const StudentForm: React.FC = () => {
    const [form, setForm] = useState({ name: '', studentClass: '', studentId: '' });

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        await axios.post('http://localhost:8080/students', form);
        alert('Student added');
    };

    return (
        <form onSubmit={handleSubmit}>
            <input name="name" placeholder="Name" onChange={handleChange} />
            <input name="studentClass" placeholder="Class" onChange={handleChange} />
            <input name="studentId" placeholder="Student ID" onChange={handleChange} />
            <button type="submit">Add Student</button>
        </form>
    );
};

export default StudentForm;
