import React, { useState } from 'react';
import axios from 'axios';

const StudentForm: React.FC = () => {
    const [form, setForm] = useState({ name: '', age:'', className: '', vaccinationStatus: '' });

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        await axios.post('http://localhost:8080/students', form);
        alert('Student added');
    };

    return (
        <form onSubmit={handleSubmit}>
            <input name="name" placeholder="Name" onChange={handleChange} />
            <input name="age" placeholder="Age" onChange={handleChange} />
            <input name="className" placeholder="class name" onChange={handleChange} />
            <select name="vaccinationStatus" onChange={handleChange}>
                <option value="">Select Vaccination Status</option>
                <option value="Vaccinated">Vaccinated</option>
                <option value="Not Vaccinated">Not Vaccinated</option>
            </select>
            <button type="submit">Add Student</button>
        </form>
    );
};

export default StudentForm;
