import React, { useEffect, useState } from 'react';
import { getAllDrives } from '../services/driveService';

interface Drive {
    id: string;
    vaccineName: string;
    driveDate: string;
    applicableClasses?: string;
    availableDoses?: string;
}

const DriveList: React.FC = () => {
    const [drives, setDrives] = useState<Drive[]>([]);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchDrives = async () => {
            try {
                const data = await getAllDrives();
                setDrives(data);
                console.log(data);
            } catch (err) {
                setError('Failed to fetch drives');
            } finally {
                setLoading(false);
            }
        };

        fetchDrives();
    }, []);

    if (loading) return <p>Loading drives...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div>
            <h2>Vaccination Drives</h2>
            <table className="table table-striped">
                <thead>
                <tr>
                    <th>Id</th>
                    <th>Vaccine</th>
                    <th>Date</th>
                    <th>Applicable Classes</th>
                    <th>availableDoses</th>
                </tr>
                </thead>
                <tbody>
                {drives.map((drive) => (
                    <tr key={drive.id}>
                        <td>{drive.vaccineName}</td>
                        <td>{new Date(drive.driveDate).toLocaleDateString()}</td>
                        <td>{drive.applicableClasses || 'N/A'}</td>
                        <td>{drive.availableDoses || 'N/A'}</td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default DriveList;
