// StudentForm.tsx
import React from 'react';
import {
    Drawer,
    Box,
    TextField,
    Button,
    MenuItem,
    Typography
} from '@mui/material';

interface Props {
    open: boolean;
    onClose: () => void;
    onSubmit: (form: any) => void;
}

const StudentForm: React.FC<Props> = ({ open, onClose, onSubmit }) => {
    const [form, setForm] = React.useState({
        name: '',
        studentClass: '',
        studentId: '',
        vaccinationStatus: ''
    });

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = () => {
        onSubmit(form);
        onClose();
    };

    return (
        <Drawer anchor="right" open={open} onClose={onClose}>
            <Box sx={{ width: 350, p: 3 }}>
                <Typography variant="h6" gutterBottom>
                    Add Student
                </Typography>
                <TextField
                    fullWidth
                    label="Name"
                    name="name"
                    margin="normal"
                    onChange={handleChange}
                />
                <TextField
                    fullWidth
                    label="Class"
                    name="studentClass"
                    margin="normal"
                    onChange={handleChange}
                />
                <TextField
                    fullWidth
                    label="Student ID"
                    name="studentId"
                    margin="normal"
                    onChange={handleChange}
                />
                <TextField
                    select
                    fullWidth
                    label="Vaccination Status"
                    name="vaccinationStatus"
                    margin="normal"
                    onChange={handleChange}
                >
                    <MenuItem value="Vaccinated">Vaccinated</MenuItem>
                    <MenuItem value="Not Vaccinated">Not Vaccinated</MenuItem>
                </TextField>
                <Button
                    variant="contained"
                    color="primary"
                    fullWidth
                    sx={{ mt: 2 }}
                    onClick={handleSubmit}
                >
                    Submit
                </Button>
            </Box>
        </Drawer>
    );
};

export default StudentForm;
